diff --git a/src/include/duckdb/main/settings.hpp b/src/include/duckdb/main/settings.hpp
index 92fabefc4b2f..a9d70af8426c 100644
--- a/src/include/duckdb/main/settings.hpp
+++ b/src/include/duckdb/main/settings.hpp
@@ -185,6 +185,7 @@ struct EnableProgressBarPrintSetting {
 	    "Controls the printing of the progress bar, when 'enable_progress_bar' is true";
 	static constexpr const LogicalTypeId InputType = LogicalTypeId::BOOLEAN;
 	static void SetLocal(ClientContext &context, const Value &parameter);
+	static void ResetLocal(ClientContext &context);
 	static Value GetSetting(ClientContext &context);
 };

diff --git a/src/main/settings/settings.cpp b/src/main/settings/settings.cpp
index b6928aaffd60..c28ed298d3bf 100644
--- a/src/main/settings/settings.cpp
+++ b/src/main/settings/settings.cpp
@@ -454,6 +454,10 @@ void EnableProgressBarPrintSetting::SetLocal(ClientContext &context, const Value
 	ClientConfig::GetConfig(context).print_progress_bar = input.GetValue<bool>();
 }

+void EnableProgressBarPrintSetting::ResetLocal(ClientContext &context) {
+	ClientConfig::GetConfig(context).print_progress_bar = ClientConfig().print_progress_bar;
+}
+
 Value EnableProgressBarPrintSetting::GetSetting(ClientContext &context) {
 	return Value::BOOLEAN(ClientConfig::GetConfig(context).print_progress_bar);
 }
