+            ApplyEverything();
         }
         if (PDisksResponse && PDisksResponse->IsDone() && !PDisksProcessed) {
             if (PDisksResponse->IsOk()) {
@@ -2112,6 +2104,7 @@ class TJsonNodes : public TViewerPipeClient {
                 AddProblem("bsc-pdisks-no-data");
